import React from 'react'

const initialState = {
    modules: [
        {_id:123, title:"module 123"},
        {_id:234, title:"module 234"},
        {_id:345, title:"module 345"}
    ]
}

const moduleReducer = (state=initialState, action) => {
    switch (action.type) {
        case "FIND_MODULES_FOR_COURSES":
            return {
                ...state,
                modules: action.modules
            }
        case "CREATE_MODULE":
            const newState = {
                modules:[
                    ...state.modules,
                    action.module
                    // {
                    //     title:"New Module",
                    //     _id: (new Date()).getTime()   //DELETE WHEN SENDING TO THE SERVER//
                    // }
                ]
            }
            return newState

        case "DELETE_MODULE":
            alert("Delete the module" + action.moduleToDelete.title)
            const newState1 = {
                modules: state.modules.filter(module =>{
                    if(module._id === action.moduleToDelete) {
                        return false
                    }else{
                        return true
                    }
                })
            }
            return newState1

        case"UPDATE_MODULE":
            return{
                modules: state.modules.map(m =>{
                    if(m._id === action.module._id) {
                        return action.module
                    }else{
                        return m
                    }
                })
            }
        default:
            return state
    }
}

export default moduleReducer
