import React from 'react'
import CourseRow from "./course-row";

class CourseTable extends React.Component{
    constructor(props) {
        super(props);
        console.log(props)
    }
    state = {
        courses: [
            {title:"CS5210", owner:"me", lastModified:"1/1/2021"},
            {title:"CS5310", owner:"you", lastModified:"2/1/2021"},
            {title:"CS5410", owner:"him", lastModified:"3/1/2021"},
            {title:"CS5510", owner:"her", lastModified:"4/1/2021"},
            {title:"CS5610", owner:"us", lastModified:"1/1/2021"},
            {title:"CS5710", owner:"me", lastModified:"1/2/2021"}
        ]
    }
    // courses = [
    //     {title:"CS5210", owner:"me", lastModified:"1/1/2021"},
    //     {title:"CS5310", owner:"you", lastModified:"2/1/2021"},
    //     {title:"CS5410", owner:"him", lastModified:"3/1/2021"},
    //     {title:"CS5510", owner:"her", lastModified:"4/1/2021"},
    //     {title:"CS5610", owner:"us", lastModified:"1/1/2021"},
    //     {title:"CS5710", owner:"me", lastModified:"1/2/2021"}
    // ]
    addCourse = () =>{
        alert('add course')
        const newCourse ={
            title:"New Course",
            owner:"Me",
            lastModified:"1/1/2021"
        }
        this.state.courses.push(newCourse)
        this.setState(this.state)
    }
    render() {
        return(
            <div>
                <h2>Course Table</h2>
                <button onClick={this.addCourse}>
                    Add Course
                </button>
                <table className="table">
                   {/*<CourseRow title="CS5610" owner="me"/>*/}
                   {/*<CourseRow title="CS4610" owner="you"/>*/}
                   {/*<CourseRow title="CS3610" owner="him"/>*/}
                   {/*<CourseRow title="CS2610" owner="she"/>*/}
                    {
                        this.state.courses.map(course =>
                            <CourseRow
                                course={course}
                                title={course.title}
                                lastModified={course.lastModified}
                                owner={course.owner}/>)
                    }
                </table>
            </div>
        )
    }
}
export default CourseTable
