import React from 'react'
import {connect} from 'react-redux'
import EditableItem from "../editable-item";

const ModuleList = (
    {
        myModules=[],
        createModule= ()=> alert("Create Module 234"),
        deleteModule= ()=> alert("Delete" + item._id)
    }) =>
    <div>
        <h2>Modules {myModules.length}</h2>
        <ul className="list-group">
            {
                myModules.map(module =>
                <li className="list-group-item">
                    <EditableItem
                        deleteItem={(item) => alert("Delete" + item._id)}
                        item={module}/>
                </li>
                )
            }
            <li className="list-group-item">
                <i onClick={createModule} className="fas fa-plus fa-2x"></i>
            </li>
        </ul>
    </div>

const stpm = (state) =>{
    return{
        myModules: state.moduleReducer.modules
    }
}
const dtpm = (dispatch) =>{
    return {
        createModule: () => dispatch({type:"CREATE_MODULE"})
    }
}

export default connect(stpm,dtpm)(ModuleList)
