import React from 'react'
import CourseTable from "./course-table";
import CourseGrid from "./course-grid";
import CourseEditor from "./course-editor";
import {Route} from "react-router-dom";
import courseService, {findAllCourses, deleteCourse} from "../services/course-service";

class CourseManager extends React.Component{
    state = {
        courses: [

            // {title:"CS5210", owner:"me", lastModified:"1/1/2021"},
            // {title:"CS5310", owner:"you", lastModified:"2/1/2021"},
            // {title:"CS5410", owner:"him", lastModified:"3/1/2021"},
            // {title:"CS5510", owner:"her", lastModified:"4/1/2021"},
            // {title:"CS5610", owner:"us", lastModified:"1/1/2021"},
            // {title:"CS5710", owner:"me", lastModified:"1/2/2021"}
        ]
    }

    componentDidMount = () =>
        // findAllCourses().then(actualCourses => this.setState({
        //     courses: actualCourses
        // }))

        findAllCourses().then(courses => this.setState({courses}))


    addCourse = () =>{
        alert('DAMNNN!!!Coding Is Your Passion!!!')

        const newCourse ={
            title:"New Course",
            owner:"Me",
            lastModified:"1/1/2021"
        }
        courseService.createCourse(newCourse)
            .then(course => this.setState((prevState) => ({
                ...prevState,
                course
            })))

        // this.state.courses.push(newCourse)
        // this.setState(this.state)
    }

    deleteCourse = (courseToDelete) => {
        // console.log(course)

        courseService.deleteCourse(courseToDelete._id).then(status => {
            // const newCourses = this.state.courses
            //     .filter(course => course !== courseToDelete)
            // this.setState({
            //     courses: newCourses
            // })

            this.setState((prevState) => {
                let nextState = {...prevState}
                nextState.courses =
                    prevState
                        .courses
                        .filter(course => course !== courseToDelete)
                return nextState
            })
        })
    }

    render() {
        return(
            <div>
                <h1>Course Manager</h1>
                <button onClick={this.addCourse}>Add Course</button>

                <Route path="/courses/table">
                    <CourseTable
                        deleteCourse={this.deleteCourse}
                        courses={this.state.courses}/>
                </Route>

                <Route path="/courses/grid">
                <CourseGrid
                    deleteCourse={this.deleteCourse}
                    courses={this.state.courses}/>
                </Route>

                <Route path="/courses/editor"
                       render={(props) => {
                           return <CourseEditor
                               courses={this.state.courses} props={props}/>
                       }}>
                </Route>

            </div>
        )
    }

}
export default CourseManager
