import React from 'react'

const initialState = {
    modules: [
        {_id:123, title:"module 123"},
        {_id:234, title:"module 234"},
        {_id:345, title:"module 345"}
    ]
}

const moduleReducer = (state=initialState, action) => {
    switch (action.type) {
        case "CREATE_MODULE":
            const newState = {
                modules:[
                    ...state.modules,
                    {
                        title:"New Module",
                        _id: (new Date()).getTime()   //DELETE WHEN SENDING TO THE SERVER//
                    }
                ]
            }
            return newState
        case "DELETE_MODULE":
            alert("Delete the module" + action)
        case"UPDATE_MODULE":
        default:
            return state
    }
}

export default moduleReducer
